    private void confirmOptionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_confirmOptionActionPerformed
        Markbook book = new Markbook();
        int courseIndex = book.getCourse(this.courseName.getText(), Integer.parseInt(this.periodOutput.getText()));
        Course course = book.courses.get(courseIndex);
        if (this.teacherAction.getSelectedItem().toString().equals("Add Student")) {
            boolean success;
            String userID = JOptionPane.showInputDialog("Enter student number: ");
            success = book.addStudentToCourse(this.courseName.getText(), Integer.parseInt(this.periodOutput.getText()), Integer.parseInt(userID));
            if (success) {
                book.courses.get(courseIndex).courseOutputTeacher(Integer.parseInt(this.teacherNum.getText()), this.teacherName.getText());
                book.saveCourses();
                this.dispose();
            } else {
                JOptionPane.showMessageDialog(this, "Error. Student not created.");
            }
        } else if (this.teacherAction.getSelectedItem().toString().equals("Delete Student")){
            boolean success;
            String userID = JOptionPane.showInputDialog("Enter student number: ");
            success = book.deleteStudent(this.courseName.getText(), Integer.parseInt(this.periodOutput.getText()), Integer.parseInt(userID));
            if (success) {
                book.courses.get(courseIndex).courseOutputTeacher(Integer.parseInt(this.teacherNum.getText()), this.teacherName.getText());
                book.saveCourses();
                this.dispose();
            } else {
                JOptionPane.showMessageDialog(this, "Error. Student not deleted.");
            }
        } else if (this.teacherAction.getSelectedItem().toString().equals("Find Student")){ 
            String userID = JOptionPane.showInputDialog("Enter student number: ");
            int stuIndex = book.courses.get(courseIndex).checkStudent(Integer.parseInt(userID));
            if (stuIndex != -1) {
                book.courses.get(courseIndex).courseOutputStudent(Integer.parseInt(userID), userID);
            }
        } else if (this.teacherAction.getSelectedItem().toString().equals("Add Course")){ 
            String name = JOptionPane.showInputDialog("Enter course name: ");
            String period = JOptionPane.showInputDialog("Enter period number: ");
            if (book.addCourse(name, Integer.parseInt(period), Integer.parseInt(this.teacherNum.getText()))) {
                book.courses.get(book.courses.size()-1).courseOutputTeacher(Integer.parseInt(this.teacherNum.getText()), this.teacherName.getText());
            }
        }
    }//GEN-LAST:event_confirmOptionActionPerformed

    private void confirmSortActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_confirmSortActionPerformed
        String sortSelection = this.sortSelect.getSelectedItem().toString();
        Markbook book = new Markbook();
        int courseIndex = book.getCourse(this.courseName.getText(), Integer.parseInt(this.periodOutput.getText()));
        if (sortSelection.equals("Increasing Mark")) {
            book.courses.get(courseIndex).sortIncreasing();
            book.courses.get(courseIndex).courseOutputTeacher(Integer.parseInt(this.teacherNum.getText()), this.teacherName.getText());
            this.dispose();
        } else {
            book.courses.get(courseIndex).sortDecreasing();
            book.courses.get(courseIndex).courseOutputTeacher(Integer.parseInt(this.teacherNum.getText()), this.teacherName.getText());
            this.dispose();
        }
    }//GEN-LAST:event_confirmSortActionPerformed

