    private void confirmDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_confirmDeleteActionPerformed
        Markbook book = new Markbook();
        int studentID = Integer.parseInt(this.deleteID.getText());
        String entry = this.deleteEntry.getText();
        int courseIndex = book.getCourse(this.courseName.getText(), Integer.parseInt(this.periodOutput.getText()));
        boolean success = book.courses.get(courseIndex).deleteEntry(studentID, entry);
        if (success) {
            book.courses.get(courseIndex).courseOutputTeacher(Integer.parseInt(this.teacherNum.getText()), this.teacherName.getText());
            this.dispose();
        } else {
            JOptionPane.showMessageDialog(this, "Error. Entry not deleted.");
        }
    }//GEN-LAST:event_confirmDeleteActionPerformed

    private void addEntryActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addEntryActionPerformed
        Markbook book = new Markbook();
        int courseIndex = book.getCourse(this.courseName.getText(), Integer.parseInt(this.periodOutput.getText()));
        double[] entries = new double[4];
        entries[0] = Double.parseDouble(this.ku.getText());
        entries[1] = Double.parseDouble(this.app.getText());
        entries[2] = Double.parseDouble(this.comm.getText());
        entries[3] = Double.parseDouble(this.tips.getText());
        double[] totals = new double[4];
        totals[0] = Double.parseDouble(this.kuTotal.getText());
        totals[1] = Double.parseDouble(this.appTotal.getText());
        totals[2] = Double.parseDouble(this.commTotal.getText());
        totals[3] = Double.parseDouble(this.tipsTotal.getText());
        int weighting = Integer.parseInt(this.weighting.getText());
        String entryName = this.entry.getText();
        int studentNum = Integer.parseInt(this.stuNum.getText());
        String courseName = this.courseName.getText();
        int teacherNum = Integer.parseInt(this.teacherNum.getText());
        int period = Integer.parseInt(this.periodOutput.getText());
        book.addEntry(courseName, period, studentNum, entries[0], entries[1], entries[2], entries[3], totals[0], totals[1], totals[2], totals[3], weighting, entryName);
        book.courses.get(courseIndex).courseOutputTeacher(Integer.parseInt(this.teacherNum.getText()), this.teacherName.getText());
        this.dispose();
    }//GEN-LAST:event_addEntryActionPerformed

